mapper.select:
SELECT	id
FROM	member
WHERE 	id=#{id}

mapper.insert:
INSERT INTO member(
	id
	, pw
	, name
	, age
	, gender
) VALUES (
	#{id}
	,#{pw}
	,#{name}
	,#{age}
	,#{gender}
)

mapper.update:
UPDATE member
SET pw = #{pw}
	, name = #{name}
	, age = #{age}
	, gender = #{gender}
WHERE id = #{id}
